//CONDITIONAL BASED MELODIES
//Develop five parts combining 5-8 notes and rests in some cases. 
/* Use some type of filter( subtractive synthesis) or frequency modulation
or somewhere in your music */
/* Also use some type of mini-notation (like commas, brackets or angle brackets)*/
setcpm(25)
const part1 = note("d3 c3 ~ e3*2 f3 d3").s("gm_reed_organ").fm("<3 2 4>")
const part2 = note("c3 d3 e3*2").s("gm_tinkle_bell")
const part3 = note("c3 c4 c3 c4 <d3, f3, a3>").s("piano").fast(2)
const part4 = note("d5 d4 e5 e4 ~ f5 ~ f4").s("gm_flute").fm("<5 7 8>")
const part5 = note("c3 d4 e3*2, e4 f4 d4").s("gm_cello")


// User Input (Change this number as the code runs)
/*This works as our "if then" statement. 
If the user input is 3, then it plays the third library order option. 
*/
const userInput =  3


// 2. Library of Order
// Add four to five additional orders to this library to include all five of the parts. 
const orders = [
  [part1, part2, part3], // Order 1: 1-2-3
  [part4, part3, part5],
  [part2, part5, part1],
  [part3, part1, part4],
  [part5, part4, part2]
  ]

// 3. Strudel plays the order based on the number you input into the userInput constant
cat(...orders[userInput])// three dots represent the different parts

